// PromptRefinement.jsx
// React component for the prompt refinement system

import React, { useState } from 'react';
import axios from 'axios';

// Base URL for your API - update this to match your deployment
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000/api';

const PromptRefinement = ({ onComplete }) => {
  const [searchInput, setSearchInput] = useState('');
  const [isRefining, setIsRefining] = useState(false);
  const [refinementQuestion, setRefinementQuestion] = useState(null);
  const [refinementResponse, setRefinementResponse] = useState('');
  const [finalPrompt, setFinalPrompt] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Handle initial search input submission
  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    if (!searchInput.trim()) return;

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post(`${API_URL}/refine-prompt`, {
        userInput: searchInput
      });

      setRefinementQuestion(response.data);
      setIsRefining(true);
    } catch (err) {
      setError('Failed to process your search. Please try again.');
      console.error('Error getting refinement:', err);
    } finally {
      setLoading(false);
    }
  };

  // Handle user's response to refinement question
  const handleRefinementSubmit = async (e) => {
    e.preventDefault();
    if (!refinementResponse.trim() && !e.target.value) return;

    setLoading(true);
    setError(null);

    try {
      // If this was called from a button click with predefined option
      const responseValue = e.target.value || refinementResponse;

      const response = await axios.post(`${API_URL}/apply-refinement`, {
        originalPrompt: searchInput,
        refinementType: refinementQuestion.refinementType,
        userResponse: responseValue
      });

      setFinalPrompt(response.data);

      // Call the onComplete callback with the refined prompt data
      if (onComplete && typeof onComplete === 'function') {
        onComplete(response.data);
      }

    } catch (err) {
      setError('Failed to refine your search. Please try again.');
      console.error('Error applying refinement:', err);
    } finally {
      setLoading(false);
    }
  };

  // Reset the form to start over
  const handleReset = () => {
    setSearchInput('');
    setIsRefining(false);
    setRefinementQuestion(null);
    setRefinementResponse('');
    setFinalPrompt(null);
    setError(null);
  };

  // Render predefined options if available
  const renderOptions = () => {
    if (!refinementQuestion?.options || refinementQuestion.options.length === 0) {
      return null;
    }

    return (
      <div className="refinement-options">
        {refinementQuestion.options.map((option, index) => (
          <button
            key={index}
            value={option}
            onClick={handleRefinementSubmit}
            className="option-button"
            disabled={loading}
          >
            {option}
          </button>
        ))}
      </div>
    );
  };

  return (
    <div className="prompt-refinement-container">
      {!isRefining ? (
        // Step 1: Initial search input
        <form onSubmit={handleSearchSubmit} className="search-form">
          <h2>What kind of professionals are you looking for?</h2>
          <input
            type="text"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            placeholder="E.g., People in industries such as Finance, Fintech..."
            className="search-input"
            disabled={loading}
          />
          <button 
            type="submit" 
            className="search-button"
            disabled={!searchInput.trim() || loading}
          >
            {loading ? 'Processing...' : 'Search'}
          </button>
        </form>
      ) : finalPrompt ? (
        // Step 3: Show refined results and final prompt
        <div className="refined-results">
          <h2>Your search has been refined!</h2>
          <div className="final-prompt-box">
            <h3>Refined Search:</h3>
            <p>{finalPrompt.refinedPrompt}</p>

            <div className="search-params">
              <h4>Search Parameters:</h4>
              {Object.entries(finalPrompt.searchParams).map(([key, values]) => {
                if (values && values.length > 0) {
                  return (
                    <div key={key} className="param-group">
                      <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong>{' '}
                      {values.join(', ')}
                    </div>
                  );
                }
                return null;
              })}
            </div>
          </div>

          <button onClick={handleReset} className="reset-button">
            Start New Search
          </button>
        </div>
      ) : (
        // Step 2: Refinement question
        <form onSubmit={handleRefinementSubmit} className="refinement-form">
          <h2>Let's refine your search</h2>
          <div className="confirmation-question">
            {refinementQuestion?.confirmationQuestion}
          </div>

          {renderOptions()}

          <input
            type="text"
            value={refinementResponse}
            onChange={(e) => setRefinementResponse(e.target.value)}
            placeholder="Your response..."
            className="refinement-input"
            disabled={loading}
          />

          <div className="form-actions">
            <button 
              type="button" 
              onClick={() => setIsRefining(false)}
              className="back-button"
              disabled={loading}
            >
              Back
            </button>
            <button 
              type="submit" 
              className="submit-button"
              disabled={!refinementResponse.trim() || loading}
            >
              {loading ? 'Processing...' : 'Submit'}
            </button>
          </div>
        </form>
      )}

      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default PromptRefinement;
